package learn;

public class TernaryOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int number=10;
		Boolean result=(number==10)? true:false;
		System.out.println("After ternary operation ,result is: "+result);
		int resul=(number==10)?1000:2000;
		System.out.println("After ternary operation ,result is: "+resul);

	}

}
